    69	      toast.error('Failed to connect to database');
    70	      setDebugInfo({
    71	        error: error instanceof Error ? error.message : 'Unknown error',
    72	        source: 'network',
    73	        timestamp: new Date().toISOString()
    74	      });
    75	    } finally {
    76	      setIsRefreshing(false);
    77	    }
    78	  };
    79	
    80	  const handleAddUser = async () => {
    81	    if (!userName.trim() || !userEmail.trim()) {
    82	      toast.error('Please enter both name and email');
    83	      return;
    84	    }
    85	
    86	    setIsLoading(true);
    87	    setDebugInfo(null);
    88	
    89	    try {
    90	      const userData = JSON.parse(localStorage.getItem('userData') || '{}');
    91	      const workspaceCode = userData.workspaceCode || 'default';
    92	
    93	      const response = await fetch(`${API_BASE}/api/python/add-user`, {
    94	        method: 'POST',
    95	        headers: { 'Content-Type': 'application/json' },
    96	        body: JSON.stringify({
    97	          userName,
    98	          userEmail,
    99	          workspaceCode
   100	        }),
   101	      });
   102	
   103	      const result = await response.json();
   104	      setDebugInfo(result.debug || result);
   105	
   106	      if (result.success) {
   107	        const newUser: User = {
   108	          id: Date.now().toString(),
   109	          name: userName,
   110	          email: userEmail,
   111	          status: 'active',
   112	          addedDate: new Date().toISOString().split('T')[0]
   113	        };
   114	        setUsers(prev => [...prev, newUser]);
   115	        setUserName('');
   116	        setUserEmail('');
   117	        setCurrentAction(null);
   118	        toast.success(`User ${userName} added successfully with biometric data!`);
   119	        // Refresh users list to get updated data from db.json
   120	        setTimeout(() => fetchUsers(), 2000);
   121	      } else {
   122	        toast.error(result.message || 'Failed to add user');
   123	      }
   124	    } catch (error) {
   125	      const message = error instanceof Error ? error.message : 'Unknown error occurred';
   126	      toast.error('Failed to connect to backend: ' + message);
   127	      setDebugInfo({ error: message, type: 'network_error' });
   128	    } finally {
   129	      setIsLoading(false);
   130	    }
   131	  };
   132	
   133	  const handleDeleteUser = async (userId: string, userName: string) => {
   134	    setIsLoading(true);
   135	
   136	    try {
   137	      const response = await fetch(`${API_BASE}/api/python/delete-user`, {
   138	        method: 'POST',
   139	        headers: { 'Content-Type': 'application/json' },
   140	        body: JSON.stringify({ userName }),
   141	      });
   142	
   143	      const result = await response.json();
   144	
   145	      if (result.success) {
   146	        setUsers(prev => prev.filter(user => user.id !== userId));
   147	        toast.success(`User ${userName} deleted successfully`);
   148	        // Refresh users list to get updated data from db.json
   149	        setTimeout(() => fetchUsers(), 1000);
   150	      } else {
   151	        toast.error(result.message || 'Delete failed');
   152	      }
   153	    } catch (error) {
   154	      const message = error instanceof Error ? error.message : 'Unknown error occurred';
   155	      toast.error('Failed to delete user: ' + message);
   156	    } finally {
   157	      setIsLoading(false);
   158	    }
   159	  };
   160	
   161	  return (
   162	    <div className="space-y-6 md:space-y-8 px-2 md:px-0">
   163	      {/* Database Status & Refresh */}
   164	      <Card className="glass-effect-strong border-emerald-500/30">
   165	        <CardHeader className="pb-3">
   166	          <div className="flex flex-col md:flex-row md:items-center md:justify-between space-y-2 md:space-y-0">
   167	            <CardTitle className="text-emerald-400 flex items-center text-lg md:text-xl">
   168	              <Database className="w-5 h-5 mr-2" />
   169	              Database Status
   170	            </CardTitle>
   171	            <Button
   172	              onClick={fetchUsers}
   173	              disabled={isRefreshing}
   174	              className="button-modern text-sm px-3 py-2 self-start md:self-auto"
   175	            >
   176	              {isRefreshing ? (
   177	                <Loader2 className="w-4 h-4 animate-spin mr-2" />
   178	              ) : (
   179	                <RefreshCw className="w-4 h-4 mr-2" />
   180	              )}
   181	              Refresh
   182	            </Button>
   183	          </div>
   184	        </CardHeader>
   185	        <CardContent>
   186	          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
   187	            <div className="bg-emerald-500/10 p-3 rounded-lg">
   188	              <div className="text-emerald-400 font-semibold">Total Users</div>
   189	              <div className="text-white text-2xl font-bold">{users.length}</div>
   190	            </div>
   191	            <div className="bg-teal-500/10 p-3 rounded-lg">
   192	              <div className="text-teal-400 font-semibold">Active Users</div>
   193	              <div className="text-white text-2xl font-bold">
   194	                {users.filter(u => u.status === 'active').length}
   195	              </div>
   196	            </div>
   197	            <div className="bg-cyan-500/10 p-3 rounded-lg">
   198	              <div className="text-cyan-400 font-semibold">Data Source</div>
   199	              <div className="text-white text-lg font-semibold">db.json</div>
   200	            </div>
   201	          </div>
   202	          {debugInfo && (
   203	            <details className="mt-4">
   204	              <summary className="text-gray-400 cursor-pointer hover:text-white transition-colors">
   205	                Debug Information
   206	              </summary>
   207	              <pre className="text-xs text-gray-300 bg-black/30 p-3 rounded mt-2 overflow-auto max-h-32">
   208	                {JSON.stringify(debugInfo, null, 2)}
   209	              </pre>
   210	            </details>
   211	          )}
   212	        </CardContent>
   213	      </Card>
   214	
   215	      {/* Action Cards */}
   216	      {!currentAction && (
   217	        <motion.div
   218	          initial={{ opacity: 0, y: 20 }}
   219	          animate={{ opacity: 1, y: 0 }}
   220	          className="grid grid-cols-1 md:grid-cols-2 gap-4 md:gap-6"
   221	        >
   222	          <Card className="glass-effect card-hover shimmer-effect relative overflow-hidden">
   223	            <CardHeader className="text-center">
   224	              <UserPlus className="w-10 h-10 md:w-12 md:h-12 text-emerald-400 mb-4 mx-auto" />
   225	              <CardTitle className="text-white text-lg md:text-xl">Add User with Biometrics</CardTitle>
   226	              <CardDescription className="text-gray-300 text-sm md:text-base">
   227	                Register users using face and voice capture.
   228	              </CardDescription>
   229	            </CardHeader>
   230	            <CardContent>
   231	              <Button
   232	                onClick={() => setCurrentAction('add')}
   233	                className="w-full button-modern text-sm md:text-base"
   234	              >
   235	                <Plus className="w-4 h-4 mr-2" />
   236	                Add New User
   237	              </Button>
   238	            </CardContent>
   239	          </Card>
   240	
   241	          <Card className="glass-effect card-hover shimmer-effect relative overflow-hidden">
   242	            <CardHeader className="text-center">
   243	              <UserMinus className="w-10 h-10 md:w-12 md:h-12 text-rose-400 mb-4 mx-auto" />
   244	              <CardTitle className="text-white text-lg md:text-xl">Delete User</CardTitle>
   245	              <CardDescription className="text-gray-300 text-sm md:text-base">
   246	                Remove users from the biometric system.
   247	              </CardDescription>
   248	            </CardHeader>
   249	            <CardContent>
   250	              <Button
   251	                onClick={() => setCurrentAction('delete')}
   252	                className="w-full button-coral text-sm md:text-base"
   253	              >
   254	                <Trash2 className="w-4 h-4 mr-2" />
   255	                Manage Users
   256	              </Button>
   257	            </CardContent>
   258	          </Card>
   259	        </motion.div>
   260	      )}
   261	
   262	      {/* Add User Form */}
   263	      <AnimatePresence>
   264	        {currentAction === 'add' && (
   265	          <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: -20 }}>
   266	            <Card className="glass-effect-strong">
   267	              <CardHeader>
   268	                <div className="flex flex-col md:flex-row md:items-start md:justify-between space-y-2 md:space-y-0">
   269	                  <div>
   270	                    <CardTitle className="text-white flex items-center text-lg md:text-xl">
   271	                      <UserPlus className="w-5 h-5 md:w-6 md:h-6 mr-2 text-emerald-400" />
   272	                      Add User with Live Biometric Capture
   273	                    </CardTitle>
   274	                    <CardDescription className="text-gray-300 text-sm md:text-base">
   275	                      Python script will capture the user's face and voice.
   276	                    </CardDescription>
   277	                  </div>
   278	                  <Button
   279	                    variant="ghost"
   280	                    onClick={() => setCurrentAction(null)}
   281	                    className="text-gray-400 hover:text-white self-start md:self-auto"
   282	                    disabled={isLoading}
   283	                  >
   284	                    Cancel
   285	                  </Button>
   286	                </div>
   287	              </CardHeader>
   288	              <CardContent className="space-y-4 md:space-y-6">
   289	                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 md:gap-6">
   290	                  <div>
   291	                    <Label htmlFor="userName" className="text-white text-sm md:text-base">Full Name</Label>
   292	                    <Input
   293	                      id="userName"
   294	                      value={userName}
   295	                      onChange={(e) => setUserName(e.target.value)}
   296	                      placeholder="Enter full name"
   297	                      className="bg-white/10 border-white/20 text-white mt-1"
   298	                      disabled={isLoading}
   299	                    />
   300	                  </div>
   301	                  <div>
   302	                    <Label htmlFor="userEmail" className="text-white text-sm md:text-base">Email Address</Label>
   303	                    <Input
   304	                      id="userEmail"
   305	                      type="email"
   306	                      value={userEmail}
   307	                      onChange={(e) => setUserEmail(e.target.value)}
   308	                      placeholder="Enter email"
   309	                      className="bg-white/10 border-white/20 text-white mt-1"
   310	                      disabled={isLoading}
   311	                    />
   312	                  </div>
   313	                </div>
   314	
   315	                <div className="grid grid-cols-1 md:grid-cols-2 gap-3 md:gap-4">
   316	                  <div className="flex items-center space-x-3 p-3 bg-emerald-500/10 rounded-lg border border-emerald-500/20">
   317	                    <Camera className="w-5 h-5 md:w-6 md:h-6 text-emerald-400" />
   318	                    <div>
   319	                      <h4 className="text-white font-medium text-sm md:text-base">Camera Required</h4>
   320	                      <p className="text-gray-400 text-xs md:text-sm">Used for face recognition</p>
   321	                    </div>
   322	                  </div>
   323	                  <div className="flex items-center space-x-3 p-3 bg-teal-500/10 rounded-lg border border-teal-500/20">
   324	                    <Mic className="w-5 h-5 md:w-6 md:h-6 text-teal-400" />
   325	                    <div>
   326	                      <h4 className="text-white font-medium text-sm md:text-base">Microphone Required</h4>
   327	                      <p className="text-gray-400 text-xs md:text-sm">Used for voice authentication</p>
   328	                    </div>
   329	                  </div>
   330	                </div>
   331	
   332	                <Button
   333	                  onClick={handleAddUser}
   334	                  disabled={isLoading || !userName.trim() || !userEmail.trim()}
   335	                  className="w-full button-modern h-10 md:h-12 text-sm md:text-base"
   336	                >
   337	                  {isLoading ? (
   338	                    <div className="flex items-center space-x-2">
   339	                      <Loader2 className="w-4 h-4 animate-spin" />
   340	                      <span>Registering...</span>
   341	                    </div>
   342	                  ) : (
   343	                    <div className="flex items-center space-x-2">
   344	                      <UserPlus className="w-4 h-4" />
   345	                      <span>Start Biometric Registration</span>
   346	                    </div>
   347	                  )}
   348	                </Button>
   349	              </CardContent>
   350	            </Card>
   351	          </motion.div>
   352	        )}
   353	      </AnimatePresence>
   354	
   355	      {/* Delete User Form */}
   356	      <AnimatePresence>
   357	        {currentAction === 'delete' && (
   358	          <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: -20 }}>
   359	            <Card className="glass-effect-strong">
   360	              <CardHeader>
   361	                <div className="flex flex-col md:flex-row md:items-center md:justify-between space-y-2 md:space-y-0">
   362	                  <CardTitle className="text-white flex items-center text-lg md:text-xl">
   363	                    <UserMinus className="w-5 h-5 md:w-6 md:h-6 mr-2 text-rose-400" />
   364	                    Delete Users
   365	                  </CardTitle>
   366	                  <Button
   367	                    variant="ghost"
   368	                    onClick={() => setCurrentAction(null)}
   369	                    className="text-gray-400 hover:text-white self-start md:self-auto"
   370	                    disabled={isLoading}
   371	                  >
   372	                    Cancel
   373	                  </Button>
   374	                </div>
   375	              </CardHeader>
   376	              <CardContent className="space-y-3">
   377	                {users.length === 0 ? (
   378	                  <div className="text-center py-8">
   379	                    <Users className="w-12 h-12 text-gray-500 mx-auto mb-4" />
   380	                    <p className="text-gray-400">No users found in database</p>
   381	                  </div>
   382	                ) : (
   383	                  users.map((user) => (
   384	                    <div key={user.id} className="flex flex-col md:flex-row md:items-center md:justify-between p-4 bg-white/5 rounded-lg border border-white/10 space-y-3 md:space-y-0">
   385	                      <div className="flex items-center space-x-3 md:space-x-4">
   386	                        <div className="w-8 h-8 md:w-10 md:h-10 bg-gradient-to-r from-emerald-500 to-teal-500 rounded-full flex items-center justify-center">
   387	                          <span className="text-white font-semibold text-sm md:text-base">
   388	                            {user.name.split(' ').map(n => n[0]).join('')}
   389	                          </span>
   390	                        </div>
   391	                        <div className="flex-1">
   392	                          <h4 className="text-white font-semibold text-sm md:text-base">{user.name}</h4>
   393	                          <p className="text-gray-400 text-xs md:text-sm">{user.email}</p>
   394	                          <div className="flex flex-wrap items-center gap-2 mt-1">
   395	                            <p className="text-gray-500 text-xs">Added: {user.addedDate}</p>
   396	                            {user.hasFaceData && (
   397	                              <div className="flex items-center space-x-1">
   398	                                <Eye className="w-3 h-3 text-emerald-400" />
   399	                                <span className="text-xs text-emerald-400">{user.faceEmbeddings}</span>
   400	                              </div>
   401	                            )}
   402	                            {user.hasVoiceData && (
   403	                              <div className="flex items-center space-x-1">
   404	                                <Volume2 className="w-3 h-3 text-teal-400" />
   405	                                <span className="text-xs text-teal-400">{user.voiceEmbeddings}</span>
   406	                              </div>
   407	                            )}
   408	                          </div>
   409	                        </div>
   410	                      </div>
   411	                      <div className="flex items-center justify-between md:justify-end space-x-3">
   412	                        <Badge variant={user.status === 'active' ? 'default' : 'secondary'} className="text-xs">
   413	                          {user.status}
   414	                        </Badge>
   415	                        <Button
   416	                          onClick={() => handleDeleteUser(user.id, user.name)}
   417	                          disabled={isLoading}
   418	                          className="button-coral text-xs px-3 py-1"
   419	                          size="sm"
   420	                        >
   421	                          {isLoading ? <Loader2 className="w-3 h-3 animate-spin" /> : <Trash2 className="w-3 h-3" />}
   422	                        </Button>
   423	                      </div>
   424	                    </div>
   425	                  ))
   426	                )}
   427	              </CardContent>
   428	            </Card>
   429	          </motion.div>
   430	        )}
   431	      </AnimatePresence>
   432	
   433	      {/* Overview */}
   434	      {!currentAction && (
   435	        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.2 }}>
   436	          <Card className="glass-effect-strong">
   437	            <CardHeader>
   438	              <CardTitle className="text-white flex items-center text-lg md:text-xl">
   439	                <Users className="w-5 h-5 md:w-6 md:h-6 mr-2 text-emerald-400" />
   440	                Current Users ({users.length})
   441	              </CardTitle>
   442	              <CardDescription className="text-gray-300 text-sm md:text-base">
   443	                All users enrolled in the biometric system from db.json
   444	              </CardDescription>
   445	            </CardHeader>
   446	            <CardContent>
   447	              {users.length === 0 ? (
   448	                <div className="text-center py-8">
   449	                  <Database className="w-12 h-12 text-gray-500 mx-auto mb-4" />
   450	                  <p className="text-gray-400 mb-2">No users found in database</p>
   451	                  <p className="text-gray-500 text-sm">Add users to see them listed here</p>
   452	                </div>
   453	              ) : (
   454	                <div className="grid gap-3 md:gap-4">
   455	                  {users.map((user) => (
   456	                    <div key={user.id} className="flex flex-col md:flex-row md:items-center md:justify-between p-3 md:p-4 bg-white/5 rounded-lg border border-white/10 space-y-2 md:space-y-0">
   457	                      <div className="flex items-center space-x-3">
   458	                        <div className="w-8 h-8 md:w-10 md:h-10 bg-gradient-to-r from-emerald-500 to-teal-500 rounded-full flex items-center justify-center">
   459	                          <span className="text-white text-sm md:text-base font-semibold">
   460	                            {user.name.split(' ').map(n => n[0]).join('')}
   461	                          </span>
   462	                        </div>
   463	                        <div className="flex-1">
   464	                          <h4 className="text-white font-medium text-sm md:text-base">{user.name}</h4>
   465	                          <p className="text-gray-400 text-xs md:text-sm">{user.email}</p>
   466	                          <div className="flex flex-wrap items-center gap-2 mt-1">
   467	                            {user.hasFaceData && (
   468	                              <div className="flex items-center space-x-1 bg-emerald-500/20 px-2 py-1 rounded text-xs">
   469	                                <Eye className="w-3 h-3 text-emerald-400" />
   470	                                <span className="text-emerald-400">Face: {user.faceEmbeddings}</span>
   471	                              </div>
