   473	                            {user.hasVoiceData && (
   474	                              <div className="flex items-center space-x-1 bg-teal-500/20 px-2 py-1 rounded text-xs">
   475	                                <Volume2 className="w-3 h-3 text-teal-400" />
   476	                                <span className="text-teal-400">Voice: {user.voiceEmbeddings}</span>
   477	                              </div>
   478	                            )}
   479	                          </div>
   480	                        </div>
