   482	                      <div className="flex items-center justify-between md:justify-end space-x-2">
   483	                        <Badge variant={user.status === 'active' ? 'default' : 'secondary'} className="text-xs">
   484	                          {user.status}
   485	                        </Badge>
   486	                        {user.status === 'active' && <CheckCircle className="w-4 h-4 text-emerald-400" />}
   487	                      </div>
   488	                    </div>
   489	                  ))}
   490	                </div>
   491	              )}
   492	            </CardContent>
   493	          </Card>
   494	        </motion.div>
   495	      )}
   496	    </div>
   497	  );
